CREATE TABLE IF NOT EXISTS public.group_changes (id UUID DEFAULT uuid_generate_v4() PRIMARY KEY, employee_id UUID REFERENCES auth.users(id), old_group TEXT NOT NULL, new_group TEXT NOT NULL, effective_date DATE NOT NULL, request_date TIMESTAMP WITH TIME ZONE DEFAULT NOW(), status TEXT DEFAULT 'approved', created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(), updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW());
